import React, {useState, useRef} from "react";
import ListaContato from "./listaContato";

export default function App () {

    //states
    const [contato, setContato] = useState({nome: '', telefone: ''})
    const [listaContato, setListaContato] = useState([])
    const nomeRef = useRef()

    //metodos
    function definirNome(evento) {
        setContato({...contato, nome: evento.target.value})
    }
    function definirTelefone(evento) {
        setContato({...contato, telefone: evento.target.value})
    }
    function adicionarContato() {
         if(contato.nome === '' || contato.telefone === '') return
        setListaContato([...listaContato, contato])
        console.table(listaContato)

        // contato duplicado
        let duplicado = listaContato.find((ct) => ct.nome === contato.nome && ct.telefone === contato.telefone)
        console.log(duplicado)

        //limpar contato
        setContato({nome: '', telefone: ''}
        )

        //focus no input nome
        nomeRef.current.focus()
    }
 
    return(
        <>
            <h1>Lista de contatos</h1>
            <hr />
            <div>
                <div> 
                    <label htmlFor="">Nome: </label> <br />
                    <input ref={nomeRef} type="text" onChange={definirNome} value={contato.nome} />
                </div>
                <div>
                    <label htmlFor="">Telefone: </label> <br />
                    <input type="text" onChange={definirTelefone} value={contato.telefone}/>
                </div>                
                <button onClick={adicionarContato}>Adicionar contato</button>
                <div>
                    <ListaContato listaContato={listaContato}/>
                </div>
            </div>
        </>
    )
}
